let selted_items = [];
$("#catgory").append(`<h1 id="all" onclick="all_p(this)">all</h1>`);
for (let c in products) {
    $("#catgory").append(`<h1 id='${c}' onclick="all_p(this)">${c}</h1>`);
}

let total = 0;

function all_items() {
    selted_items = [];
    total = 0;
    for (let c in products) {
        for (let p in products[c]) {
            $("#display").append(`<div class="item"><h6>${products[c][p].name}</h6><img src="${products[c][p].image}" alt="image not found"><h6>${products[c][p].price}</h6></div>`);
            total += products[c][p].price;

        }
    }
    $("h1").css("background-color", "");
    $("#all").css("background-color", "red");
    $("#total").html(`Total : ${total}`);
}
all_items();

function all_p(x) {

    if (x.id == "all") {
        $("#display").html("");
        all_items()
    }
    else {
        if (!selted_items.includes(x.id)) {
            selted_items.push(x.id);
            $("#all").css("background-color", "");
            $(`#${x.id}`).css("background-color", "red");
            total = 0;
        }
        else {
            let del_index = selted_items.indexOf(x.id);
            selted_items.splice(del_index, 1);
            $(`#${x.id}`).css("background-color", "");
            total = 0;
        }
        $("#display").html("");
        console.log(total);
        for (let c in selted_items) {
            for (let p in products[selted_items[c]]) {
                $("#display").append(`<div class="item"><h6>${products[selted_items[c]][p].name}</h6><img src="${products[selted_items[c]][p].image}" alt="image not found"><h6>${products[selted_items[c]][p].price}</h6></div>`);
                total += products[selted_items[c]][p].price;
            }
        }
        $("#total").html(`Total : ${total}`);
    }
}




